import{_ as s,c as a,o as l,V as n}from"./chunks/framework.9928bc23.js";const h=JSON.parse('{"title":"html5","description":"","frontmatter":{"layout":"doc","lastUpdated":true},"headers":[],"relativePath":"web-study/studynotes/htmlnotes/html02.md","lastUpdated":null}'),t={name:"web-study/studynotes/htmlnotes/html02.md"},p=n(`<h1 id="html5" tabindex="-1">html5 <a class="header-anchor" href="#html5" aria-label="Permalink to &quot;html5&quot;">​</a></h1><h2 id="_1-什么是html5" tabindex="-1">1.什么是html5 <a class="header-anchor" href="#_1-什么是html5" aria-label="Permalink to &quot;1.什么是html5&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">注意</p><p>HTML5是HTML最新的修订版本，2014年10月由万维网联盟（W3C）完成标准制定。</p><p>HTML5的设计目的是为了在移动设备上支持多媒体。</p></div><div class="tip custom-block"><p class="custom-block-title">html5</p><p>html5是下一代标准</p><p>HTML5 是 W3C 与 WHATWG(Web Hypertext Application Technology Working Group) 合作的结果</p><p>WHATWG 致力于 web 表单和应用程序，而 W3C 专注于 XHTML 2.0</p><p>在 2006 年，双方决定进行合作，来创建一个新版本的 HTML。</p></div><blockquote><p>html5中新增的一些新特性：</p><ul><li>用于绘画的 canvas 元素</li><li>用于媒介回放的 video 和 audio 元素</li><li>对本地离线存储的更好的支持</li><li>新的特殊内容元素，比如 article、footer、header、nav、section</li><li>新的表单控件，比如 calendar、date、time、email、url、search</li></ul></blockquote><p>html5的骨架</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;!</span><span style="color:#F07178;">DOCTYPE</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">html</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">html</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">head</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">meta</span><span style="color:#89DDFF;"> </span><span style="color:#C792EA;">charset</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">utf-8</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">title</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">文档标题</span><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">title</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">head</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;"> </span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">body</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">		文档内容......</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">body</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">html</span><span style="color:#89DDFF;">&gt;</span></span></code></pre></div><h2 id="_2-html5相比于html4多了哪些东西" tabindex="-1">2.html5相比于html4多了哪些东西 <a class="header-anchor" href="#_2-html5相比于html4多了哪些东西" aria-label="Permalink to &quot;2.html5相比于html4多了哪些东西&quot;">​</a></h2><blockquote><p>1.html5 支持多媒体</p><blockquote><p>video 视频</p><p>audio 音频</p></blockquote><p>2.html5 支持开发应用</p><blockquote><p>XHTMLHttpRequest 2</p><p>本地数据存储</p><p>访问本地文件</p><p>本地 SQL 数据</p><p>缓存引用</p><p>Javascript 工作者</p></blockquote><p>3.html5 支持图形</p><blockquote><p>使用 canvas 元素</p><p>使用内联 svg</p><p>使用 <a href="https://www.runoob.com/css3/css3-2dtransforms.html" target="_blank" rel="noreferrer">CSS3 2D 转换</a>、<a href="https://www.runoob.com/css3/css3-3dtransforms.html" target="_blank" rel="noreferrer">CSS3 3D 转换</a></p></blockquote><p>4.html5 全面支持css3</p><blockquote><ul><li>新选择器</li><li>新属性</li><li>动画</li><li>2D/3D 转换</li><li>圆角</li><li>阴影效果</li><li>可下载的字体</li></ul></blockquote><p>5.html5 增加了很多语义标签</p><blockquote><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">header</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">header</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">   定义了文档的头部区域</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">nav</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">nav</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">         定义导航链接的部分</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">aside</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">aside</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> 	定义页面的侧边栏内容</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">article</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">article</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> 定义页面独立的内容区域</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">footer</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">footer</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">   定义 section 或 document 的页脚</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">section</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">section</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> 定义文档中的节（section、区段）</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">main</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">main</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">progress</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">progress</span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;">定义任何类型的任务的进度</span></span></code></pre></div></blockquote><p>6.html5 表单添加了新表单元素, 新属性，新输入类型，自动验证</p><p>7.html5 删除了一些无用的标签</p><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">acronym</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">applet</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">basefont</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">big</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">center</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">dir</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">font</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">frame</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">frameset</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">noframes</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">- </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">strike</span><span style="color:#89DDFF;">&gt;</span></span></code></pre></div></blockquote><h2 id="_3-浏览器的支持" tabindex="-1">3.浏览器的支持 <a class="header-anchor" href="#_3-浏览器的支持" aria-label="Permalink to &quot;3.浏览器的支持&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">html5对于浏览器的支持</p><p>对于现在大部分的浏览器都得到很好的支持</p><p>但是 像 IE9 以下的都不支持了</p></div><h2 id="_4-canvas标签" tabindex="-1">4.canvas标签 <a class="header-anchor" href="#_4-canvas标签" aria-label="Permalink to &quot;4.canvas标签&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">canvas标签</p><p>定义图形的，但是要靠js脚本来绘制图形</p><p>简而言之，这个标签是个画布，js脚本是画笔</p><p>具体的玩法请查阅<a href="https://developer.mozilla.org/zh-CN/docs/Web/API/Canvas_API" target="_blank" rel="noreferrer">mdn</a>进行学习</p></div><h2 id="_5-svg矢量图" tabindex="-1">5.SVG矢量图 <a class="header-anchor" href="#_5-svg矢量图" aria-label="Permalink to &quot;5.SVG矢量图&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">SVG矢量图</p><p>可以使用svg标签定义可缩放矢量图形</p></div><blockquote><p>优势:</p><ul><li>SVG 图像可通过文本编辑器来创建和修改</li><li>SVG 图像可被搜索、索引、脚本化或压缩</li><li>SVG 是可伸缩的</li><li>SVG 图像可在任何的分辨率下被高质量地打印</li><li>SVG 可在图像质量不下降的情况下被放大</li></ul></blockquote><p>SVG 与 canvas 的区别:</p><blockquote><p>SVG 是一种使用 XML 描述 2D 图形的语言。</p><p>Canvas 通过 JavaScript 来绘制 2D 图形。</p><p>SVG 不依赖分辨率，而 canvas依赖</p><p>SVG 基于 XML，这意味着 SVG DOM 中的每个元素都是可用的。您可以为某个元素添加 JavaScript 事件处理 canvas不行</p></blockquote><p>详细去<a href="https://developer.mozilla.org/zh-CN/docs/Web/SVG" target="_blank" rel="noreferrer">mdn</a>了解</p><h2 id="_6-本地数据存储" tabindex="-1">6.本地数据存储 <a class="header-anchor" href="#_6-本地数据存储" aria-label="Permalink to &quot;6.本地数据存储&quot;">​</a></h2><div class="info custom-block"><p class="custom-block-title">html5 存储</p><p>早期的时候浏览器是通过cookie来存储数据的</p><p>但是web存储要求更安全更快速，这些数据不会存在服务器上，它只用于用户请求网站数据上</p><p>它也可以存储大量的数据，不影响网站的性能</p><p>数值以 键/值 对的形式存储，该网页的数据只允许该网页访问使用</p></div><h3 id="_6-1-localstorage" tabindex="-1">6.1-localStorage <a class="header-anchor" href="#_6-1-localstorage" aria-label="Permalink to &quot;6.1-localStorage&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">localStorage存储</p><p>它可以存储整个网站的数据</p><p>保存的数据没有时间限制，也称永久存储</p><p>除非自己手动删除</p></div><h3 id="_6-2-sessionstorage" tabindex="-1">6.2-sessionstorage <a class="header-anchor" href="#_6-2-sessionstorage" aria-label="Permalink to &quot;6.2-sessionstorage&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">sessionStorage存储</p><p>它可以临时存储同一窗口或者(同一标签页)的数据</p><p>如果窗口或者(标签页)关闭时，数据也会随之删除</p></div><blockquote><p>不管是localStorage,还是sessionStorage，使用的api基本相同</p></blockquote><div class="language-js"><button title="Copy Code" class="copy"></button><span class="lang">js</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">常用的api有以下几个</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">localStorage</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">setItem</span><span style="color:#A6ACCD;">(key</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> value)   </span><span style="color:#676E95;font-style:italic;">//保存数据</span></span>
<span class="line"><span style="color:#A6ACCD;">localStorage</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">getItem</span><span style="color:#A6ACCD;">(key)          </span><span style="color:#676E95;font-style:italic;">//获取数据</span></span>
<span class="line"><span style="color:#A6ACCD;">localStorage</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">removeItem</span><span style="color:#A6ACCD;">(key)       </span><span style="color:#676E95;font-style:italic;">//删除某一条数据</span></span>
<span class="line"><span style="color:#A6ACCD;">localStorage</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">clear</span><span style="color:#A6ACCD;">()               </span><span style="color:#676E95;font-style:italic;">//清除所有数据</span></span>
<span class="line"><span style="color:#A6ACCD;">localStorage</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">key</span><span style="color:#A6ACCD;">(index)            </span><span style="color:#676E95;font-style:italic;">//得到某个索引的key值</span></span></code></pre></div><h2 id="_7-语义化标签" tabindex="-1">7.语义化标签 <a class="header-anchor" href="#_7-语义化标签" aria-label="Permalink to &quot;7.语义化标签&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">语义化标签</p><p>顾名思义，就是有语义的标签</p><p>它能够告诉开发者和浏览器，这个标签所表示的含义</p><p>div 和 span 标签 就是无语义</p><p>form、table、img等 就是有语义的</p><p>html5 现在又提供了一些新的语义标签</p><p>这些标签其实就是有语义的div</p></div><div class="language-html"><button title="Copy Code" class="copy"></button><span class="lang">html</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">//定义文档的头部区域</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">header</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">header</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">//定义导航链接的部分</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">nav</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">nav</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">//定义主要内容区域</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">main</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">main</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">//定义文档中的节（section、区段）。比如章节、页眉、页脚或文档中的其他部分</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">section</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">section</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">//定义独立的内容</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">article</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">article</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">//定义页面主区域内容之外的内容（比如侧边栏）</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">aside</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">aside</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">//文档的底部区域</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">footer</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">footer</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">//带有标题的图片或其它文件</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">figure</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">    //经常和figure连用，图片或其它文件的文字说明</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">figcaption</span><span style="color:#89DDFF;">&gt;&lt;/</span><span style="color:#F07178;">figcaption</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">figure</span><span style="color:#89DDFF;">&gt;</span></span></code></pre></div><h2 id="_8-媒体标签" tabindex="-1">8.媒体标签 <a class="header-anchor" href="#_8-媒体标签" aria-label="Permalink to &quot;8.媒体标签&quot;">​</a></h2><h3 id="_8-1-audio标签" tabindex="-1">8.1-audio标签 <a class="header-anchor" href="#_8-1-audio标签" aria-label="Permalink to &quot;8.1-audio标签&quot;">​</a></h3><blockquote><p>定义声音 音乐或其他音频流</p><p>目前这个标签仅支持 MP3、Wav、Ogg</p></blockquote><table><thead><tr><th>autoplay</th><th>autoplay</th><th>如果出现该属性，则音频在就绪后马上播放</th></tr></thead><tbody><tr><td>controls</td><td>controls</td><td>如果出现该属性，则向用户显示音频控件（比如播放/暂停按钮）</td></tr><tr><td>loop</td><td>loop</td><td>如果出现该属性，则每当音频结束时重新开始播放</td></tr><tr><td>muted</td><td>muted</td><td>如果出现该属性，则音频输出为静音</td></tr><tr><td>preload</td><td>auto metadata none</td><td>规定当网页加载时，音频是否默认被加载以及如何被加载</td></tr><tr><td>src</td><td><em>URL</em></td><td>规定音频文件的 URL</td></tr></tbody></table><h3 id="_8-2-video标签" tabindex="-1">8.2-video标签 <a class="header-anchor" href="#_8-2-video标签" aria-label="Permalink to &quot;8.2-video标签&quot;">​</a></h3><blockquote><p>定义视频 电影片段或其他视频流</p><p>支持三种视频格式：MP4、WebM、Ogg</p></blockquote><table><thead><tr><th>autoplay</th><th>autoplay</th><th>如果出现该属性，则视频在就绪后马上播放</th></tr></thead><tbody><tr><td>controls</td><td>controls</td><td>如果出现该属性，则向用户显示控件，比如播放按钮</td></tr><tr><td>height</td><td><em>pixels</em></td><td>设置视频播放器的高度</td></tr><tr><td>loop</td><td>loop</td><td>如果出现该属性，则当媒介文件完成播放后再次开始播放</td></tr><tr><td>muted</td><td>muted</td><td>如果出现该属性，视频的音频输出为静音</td></tr><tr><td>poster</td><td><em>URL</em></td><td>规定视频正在下载时显示的图像，直到用户点击播放按钮。</td></tr><tr><td>preload</td><td>auto metadata none</td><td>如果出现该属性，则视频在页面加载时进行加载，并预备播放。如果使用 &quot;autoplay&quot;，则忽略该属性</td></tr><tr><td>src</td><td><em>URL</em></td><td>要播放的视频的 UR</td></tr><tr><td>width</td><td><em>pixels</em></td><td>设置视频播放器的宽</td></tr></tbody></table><h2 id="_9-新增表单控件" tabindex="-1">9.新增表单控件 <a class="header-anchor" href="#_9-新增表单控件" aria-label="Permalink to &quot;9.新增表单控件&quot;">​</a></h2>`,38),o=[p];function e(c,r,i,F,D,y){return l(),a("div",null,o)}const m=s(t,[["render",e]]);export{h as __pageData,m as default};
